DEFINE TABLE OVERWRITE script_migration SCHEMAFULL
    PERMISSIONS
        FOR select FULL
        FOR create, update, delete NONE;

DEFINE FIELD OVERWRITE script_name ON script_migration TYPE string;
DEFINE FIELD OVERWRITE executed_at ON script_migration TYPE datetime VALUE time::now() READONLY;
DEFINE FIELD OVERWRITE checksum ON script_migration TYPE option<string>;

# --- TABLE ---
DEFINE TABLE OVERWRITE subscription_tokens SCHEMAFULL
COMMENT 'Subscription Tokens table';

# --- FIELDS ---
DEFINE FIELD OVERWRITE token ON subscription_tokens TYPE string;
DEFINE FIELD OVERWRITE created_at ON TABLE subscription_tokens TYPE datetime VALUE time::now() READONLY;

# --- INDEXES ---
DEFINE INDEX OVERWRITE unique_token ON subscription_tokens COLUMNS token UNIQUE;

# --- TABLE ---
DEFINE TABLE OVERWRITE subscriptions SCHEMAFULL
COMMENT 'Subscription table';

# --- FIELDS ---
DEFINE FIELD OVERWRITE email ON subscriptions TYPE string ASSERT string::is::email($value);
DEFINE FIELD OVERWRITE name ON subscriptions TYPE string;
DEFINE FIELD OVERWRITE status ON subscriptions TYPE 'PENDING' | 'CONFIRMED' DEFAULT 'PENDING';
DEFINE FIELD OVERWRITE token ON subscriptions TYPE record<subscription_tokens>;
DEFINE FIELD OVERWRITE created_at ON TABLE subscriptions TYPE datetime VALUE time::now() READONLY;

# --- INDEXES ---
DEFINE INDEX OVERWRITE unique_email ON subscriptions COLUMNS email UNIQUE;
